import React, { Component } from "react";
import { Button, Table } from "react-bootstrap";
import axios from "axios";
import APIs from "../constants/APIs";

export default class Reports extends Component {
  constructor(props) {
    super(props);
    this.state = {
      income: [],
      expenses: [],
      totalIncome: 0,
      totalExpenses: 0,
      message: "",
      selectedMonth: "", // Add a state property for selected month
    };
  }

  onViwe = () => {
    axios.get(APIs.SUMMARY_BASE_URL).then(
      (response) => {
        this.setState({
          income: response.data.income,
          expenses: response.data.expenses,
          totalIncome: response.data.totalIncome,
          totalExpenses: response.data.totalExpenses,
          message: "",
        });
      },
      (error) => {
        console.log(error);
        this.setState({
          message: "Report not found",
        });
      }
    );
  };

  onFilter = () => {
    // Use the selected month in the API request
    axios
      .get(`${APIs.SUMMARY_BASE_URL}?month=${this.state.selectedMonth}`)
      .then(
        (response) => {
          this.setState({
            income: response.data.income,
            expenses: response.data.expenses,
            totalIncome: response.data.totalIncome,
            totalExpenses: response.data.totalExpenses,
            message: "",
          });
        },
        (error) => {
          console.log(error);
          this.setState({
            message: "Report not found",
          });
        }
      );
  };

  handleMonthChange = (event) => {
    this.setState({ selectedMonth: event.target.value });
  };

  render() {
    return (
      <div align="center">
        <h2>Reports</h2>
        <div className="my_workout">
          <table width="400px">
            <tbody align="left">
              <tr>
                <td>Generate Report</td>
                <td>
                  <input
                    type="text"
                    placeholder="Enter Month (e.g., January)"
                    value={this.state.selectedMonth}
                    onChange={this.handleMonthChange}
                  />
                </td>
                <td>
                  <Button
                    variant="primary"
                    type="submit"
                    onClick={this.onFilter}
                  >
                    <span>Filter with Month</span>
                  </Button>
                </td>
                <td>
                  <Button variant="primary" type="submit" onClick={this.onViwe}>
                    <span>View Report</span>
                  </Button>
                </td>
              </tr>
            </tbody>
          </table>
        </div>

        {/* Rest of the component remains unchanged */}
      </div>
    );
  }
}
