import React,{Component} from 'react';
import axios from 'axios';
import APIs from "../constants/APIs";
import {Button, Form} from "react-bootstrap";

export default class Categories extends Component{
    constructor(props) {
        super(props);
        this.onChangeCategoryName = this.onChangeCategoryName.bind(this);
        this.onChangeCategoryType = this.onChangeCategoryType.bind(this);
        this.onChangeCategoryBudget = this.onChangeCategoryBudget.bind(this);
        this.getCategories = this.getCategories.bind(this);
        this.onUpdate = this.onUpdate.bind(this);
        this.onRemove = this.onRemove.bind(this);
        this.onSubmit = this.onSubmit.bind(this);
        this.state = {
            categories : [],
            message: '',
            categoryId: '',
            categoryName:'',
            categoryType: '',
            categoryBudget: '',
            buttonName:'Add',
        }
        this.getCategories();
    }
    onChangeCategoryName(e) {
        this.setState({
            categoryName: e.target.value
        });
    }
    onChangeCategoryType(e) {
        this.setState({
            categoryType: e.target.value
        });
    }
    onChangeCategoryBudget(e) {
        this.setState({
            categoryBudget: e.target.value
        });
    }

    onUpdate = (Id) => {
        // this.setState({
        //     buttonName: 'Update'
        // });

