        //                 .then(res => {
        //                         console.log(res.data.message);
        //                     }
        //                 );

        //                 window.location.href = "/workouts";
        //                 this.setState({
        //                     message: 'New record added successfully'
        //                 });
        //             }
        //         );
        //     this.setState({
        //         date: '',
