import React, { Component } from "react";
import { Button, Table } from "react-bootstrap";
import axios from "axios";
import APIs from "../constants/APIs";

export default class Reports extends Component {
  constructor(props) {
    super(props);
    this.state = {
      income: [],
      expenses: [],
      totalIncome: 0,
      totalExpenses: 0,
      message: "",
      selectedMonth: "",
      selectedYear: new Date().getFullYear().toString(),
    };
  }

  onViwe = () => {
    axios.get(APIs.SUMMARY_BASE_URL).then(
      (response) => {
        this.setState({
          income: response.data.income,
          expenses: response.data.expenses,
          totalIncome: response.data.totalIncome,
          totalExpenses: response.data.totalExpenses,
          message: "",
        });
      },
      (error) => {
        console.log(error);
        this.setState({
          message: "Report not found",
        });
      }
    );
  };

  onFilter = () => {
    const { selectedMonth, selectedYear } = this.state;

    // Check if a valid month is selected
    if (selectedMonth.trim() === "") {
      this.setState({
        message: "Please enter a valid month.",
      });
      return;
    }

    // Use the selected month and year in the API request
    const yearMonth = `${selectedYear}-${selectedMonth}`;

    axios.get(`${APIs.SUMMARY_BASE_URL}/byYearMonth?yearMonth=${yearMonth}`).then(
      (response) => {
        this.setState({
          income: response.data.income,
          expenses: response.data.expenses,
          totalIncome: response.data.totalIncome,
          totalExpenses: response.data.totalExpenses,
          message: "",
        });
      },
      (error) => {
        console.log(error);
        this.setState({
          message: "Report not found",
        });
      }
    );
  };

  handleMonthChange = (event) => {
    // Update the selected month when the user selects a month
    this.setState({ selectedMonth: event.target.value });
  };

  handleYearChange = (event) => {
    // Update the selected year when the user selects a year
    this.setState({ selectedYear: event.target.value });
  };

  render() {
    return (
      <div align="center">
        <h2>Reports</h2>
        <div className="my_workout">
          <table width="400px">
            <tbody align="left">
              <tr>
                <td>Generate Report</td>
                <td>
                  <select
                    value={this.state.selectedYear}
                    onChange={this.handleYearChange}
                  >
                    {/* You can dynamically generate year options based on your requirement */}
                    <option value="2022">2022</option>
                    <option value="2023">2023</option>
                    <option value="2024">2024</option>
                    {/* Add more years as needed */}
                  </select>
                </td>
                <td>
                  <select
                    value={this.state.selectedMonth}
                    onChange={this.handleMonthChange}
                  >
                    {/* You can dynamically generate month options based on your requirement */}
                    <option value="01">January</option>
                    <option value="02">February</option>
                    <option value="03">March</option>
                    {/* Add more months as needed */}
                  </select>
                </td>
                <td>
                  <Button
                    variant="primary"
                    type="submit"
                    onClick={this.onFilter}
                  >
                    <span>Filter with Month</span>
                  </Button>
                </td>
                <td>
                  <Button variant="primary" type="submit" onClick={this.onViwe}>
                    <span>View Report</span>
                  </Button>
                </td>
              </tr>
            </tbody>
          </table>
        </div>

        {/* Rest of the component remains unchanged */}
      </div>
    );
  }
}
